public class Task3 {

	private static String vowelsOnly(String input) {
		
		return input.replaceAll("[^a,e,i,o,u]", "");	
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println(vowelsOnly("asghar"));
		
		//break till 1:20
		
		
	}

}
